import React from "react";
import styled from "styled-components";

// 분홍 하트, 회색 하트 이미지 가져오기
import heart_pink from "../shared/heart_pink.png";
import heart_gray from "../shared/heart_gray.png";
import { useSelector } from "react-redux";

// 하트 버튼은 일단 모양새만 잡아줄거예요!
const HeartButton = (props) => {
  const grayHeart = useSelector((store) => store.post.like_cnt);
  console.log(grayHeart);

  const { _onClick } = props;
  const styles = {
    _onClick,
  };
  if (grayHeart) {
    return (
      <React.Fragment>
        <Heart icon_url={heart_gray} onClick={_onClick}></Heart>
      </React.Fragment>
    );
  }
  return (
    <React.Fragment>
      <Heart icon_url={heart_pink} onClick={_onClick}></Heart>
    </React.Fragment>
  );
};
HeartButton.defaultProps = {
  _onClick: () => {},
};
const Heart = styled.div`
  width: 30px;
  height: 30px;
  display: flex;
  background: url(${(props) => props.icon_url});
  background-size: cover;
  cursor: pointer;
`;

export default HeartButton;
