import React from "react";
import styled from "styled-components";

// 분홍 하트, 회색 하트 이미지 가져오기
import heart_pink from "../shared/heart_pink.png";
import heart_gray from "../shared/heart_gray.png";

import { history } from "../redux/configureStore";
import { useDispatch, useSelector } from "react-redux";
import { actionCreators as likeActions } from "../redux/modules/like";

// 하트 버튼은 일단 모양새만 잡아줄거예요!
const HeartButton = (props) => {
  // const grayHeart = useSelector((state) => state.post.initialPost.like_cnt);
  // console.log(grayHeart);
  console.log(props);
  const like_list = useSelector((state) => state.like.list);
  const user_info = useSelector((state) => state.user.user);

  const { _onClick, _onChange, like } = props;
  const styles = {};

  React.useEffect(() => {
    if (like_list[props.id]?.includes(user_info?.uid)) {
      const like = {like}
    }
  });
  return (
    <React.Fragment>
      <Heart icon_url={like? heart_pink : heart_gray} onClick={_onClick}></Heart>
    </React.Fragment>
  );
};
HeartButton.defaultProps = {
  _onClick: () => {},
  _onChange: () => {},
  like: false,
};
const Heart = styled.div`
  width: 30px;
  height: 30px;
  display: flex;
  background: url(${(props) => props.icon_url});
  background-size: cover;
  cursor: pointer;
`;

export default HeartButton;
