import { createAction, handleActions } from "redux-actions";
import { produce } from "immer";
import { firestore } from "../../shared/firebase";
import firebase from "firebase";
import { actionCreators as postActions } from "./post";

const SET_LIKE = "SET_LIKE";
const ADD_LIKE = "ADD_LIKE";
const LOADING = "LOADING";

const setlike = createAction(SET_LIKE, (post_id, is_liked) => ({
  post_id,
  is_liked,
}));
const addlike = createAction(ADD_LIKE, (post_id, user_id, is_liked) => ({
  post_id,
  user_id,
  is_liked,
}));

const loading = createAction(LOADING, (is_loading) => ({ is_loading }));

const initialState = {
  list: {

  },
};

const getLikeFB = (post_id) => {
  return function (dispatch, getState, { history }) {};
};

const addLikeFB = (post_id, is_liked) => {
  return function (dispatch, getState, { history }) {
    const likeDB = firestore.collection("like");
    const user_id = getState().user.user.uid;

    let like = {
      post_id: post_id,
      user_id: user_id,
      like: is_liked,
    };
    likeDB.add(like).then((doc) => {
      const postDB = firestore.collection("post");

      const post = getState().post.list.find((l) => l.id === post_id);

      const increment = firebase.firestore.FieldValue.increment(1);

      like = { ...like, id: doc.id };

      postDB
        .doc(post_id)
        .update({ like_cnt: increment })
        .then((_post) => {
          dispatch(addlike(post_id, user_id, like.like));

          if (post) {
            dispatch(
              postActions.editPost(post_id, {
                like_cnt: parseInt(post.like_cnt) + 1,
              })
            );
          }
        });
    });
  };
};

export default handleActions(
  {
    [SET_LIKE]: (state, action) => produce(state, (draft) => {
      draft.list.is_liked = action.payload.is_liked;
    }),
    [ADD_LIKE]: (state, action) =>
      produce(state, (draft) => {
        draft.list[action.payload.post_id].push.str(action.payload.is_liked);
        // draft.is_liked = action.payload.is_liked;
      }),
    [LOADING]: (state, action) =>
      produce(state, (draft) => {
        draft.is_loading = action.payload.is_loading;
      }),
  },
  initialState
);

const actionCreators = {
  setlike,
  addlike,
  addLikeFB,
  getLikeFB,
};

export { actionCreators };
